<script lang="ts">
  const imgWidth = 170;
  let files: FileList;

  /* 同一ファイルを指定したときにon:changeが発火しないのでonclickでvalueを初期化する */
  function initProfileImage(e : MouseEvent) {
    (e.target as HTMLInputElement).value="";
  }

  function changeProfileImage() {
    if (files.length <= 0) return;

    const file = files[0];
    if(!file.type.match(/^image\/(png|jpeg|gif)$/)) return ;

    const fileReader = new FileReader();
    fileReader.onload = (function() {
      /* imageオブジェクトに読み込んでcanvasを使ってリサイズ */
      const getImage = new Image();
      getImage.onload = (function(){
        const imgHeight = getImage.height * (imgWidth / getImage.width);
        const canvas = document.createElement("canvas");
        canvas.width = imgWidth;
        canvas.height = imgHeight;
        const ctx = canvas.getContext("2d");
        ctx.drawImage(getImage,0,0,imgWidth,imgHeight);
        const base64Image = canvas.toDataURL('image/png');
        
        (document.getElementById("img_profile_image") as HTMLImageElement).src = base64Image;
      });
      getImage.src = fileReader.result as string;
    });
    fileReader.readAsDataURL(file);
  }
</script>

<div class="nes-table-responsive">
  <table class="nes-table is-bordered is-dark">
  <tbody>
    <tr>
      <td rowspan="5" style="padding: 0;border-bottom: 0;">
        <label class="label_profile_image" for="input_profile_image">
        <img id="img_profile_image" alt="character_image" src="data:image/png;base64,">
        </label>
        <input type="file" accept="image/*" id="input_profile_image" bind:files on:change={changeProfileImage} on:click={e => initProfileImage(e)} style="display:none;">
      </td>
      <td>キャラクターの外見</td>
    </tr>
    <tr>
      <td>
        <div class="nes-field is-inline">
          <label for="input_height">身長</label>
          <input type="text" id="input_height" class="nes-input is-dark">
        </div>
      </td>
    </tr>
    <tr>
      <td>
        <div class="nes-field is-inline">
          <label for="input_hair">髪の色</label>
          <input type="text" id="input_hair" class="nes-input is-dark">
        </div>
      </td>
    </tr>
    <tr>
      <td>
        <div class="nes-field is-inline">
          <label for="input_eye">瞳の色</label>
          <input type="text" id="input_eye" class="nes-input is-dark">
        </div>
      </td>
    </tr>
    <tr>
      <td>
        <div class="nes-field is-inline">
          <label for="input_skin">肌の色</label>
          <input type="text" id="input_skin" class="nes-input is-dark">
        </div>
      </td>
    </tr>
  </tbody>
  </table>
</div>

<style lang="scss">
  .label_profile_image {
    display: inline-block;

    img {
    display: block;
    width: 170px;
    height: 225px;
